import React from 'react';
import ReactDOM from 'react-dom';
import { Testing } from './testing';
import './testing.css';
 

//In the constructor of App, bind .chooseVideo() to the current 
//value of this and store it in this.chooseVideo.
export class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      color: "",
    };
    this.boxClick = this.boxClick.bind(this);
  }
  boxClick() {
    this.setState({
      color: "red"
    })
  }

  render() {
    return (
      <div className="App">
          <div className="boxClickCss"
            style={{ color: this.state.color }}
            onClick={this.boxClick}>Click Me!</div>
        <Testing boxClick={this.boxClick} />
      </div>
    )
  }
}


ReactDOM.render(
  <App />,
  document.getElementById('app')
);
