import React from 'react';
import ReactDOM from 'react-dom';
import { Video } from './Video.js';
import { Menu } from './Menu.js'; 

const VIDEOS = {
  fast: 'https://content.codecademy.com/courses/React/react_video-fast.mp4',
  slow: 'https://content.codecademy.com/courses/React/react_video-slow.mp4',
  cute: 'https://content.codecademy.com/courses/React/react_video-cute.mp4',
  eek: 'https://content.codecademy.com/courses/React/react_video-eek.mp4'
};

//In the constructor of App, bind .chooseVideo() to the current 
//value of this and store it in this.chooseVideo.
export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { src: VIDEOS.fast };
    this.chooseVideo = this.chooseVideo.bind(this);
  }


  //Define a method below named chooseVideo with one parameter named newVideo
  // Call this.setState and set this.state.src equal to VIDEOS[newVideo]
  chooseVideo(newVideo){
    this.setState({
      src: VIDEOS[newVideo]
    });
  }

  
//give <Video /> an attribute called src equal to the src property stored in this.state
// give Menu a chooseVideo attribute and equal to chooseVideo
  render() {
    return (
      <div>
        <h1>Video Player</h1>
        <Menu chooseVideo={this.chooseVideo}/>
        <Video src={this.state.src}/>
      </div>
    );
  }
}

ReactDOM.render(
  <App />, 
  document.getElementById('app')
);
