import React, { useState, useEffect } from 'react';

import { DragDropContext, Droppable } from 'react-beautiful-dnd';
import Task from './Task';

import styled from 'styled-components';

import { getDocs} from 'firebase/firestore'


const TasksContainer = styled.div`
    overflow: hidden;
`


function AllTaskList({ allTasksList, onDragEnd, allTasks, handleEditTask, handleToggleDone, handleRemoveTask }) {
    const [toDoItem, setToDoItem] = useState([]);

    useEffect(() => {
        const getTest = async () => {
            const data = await getDocs(testCollectionRef);
            setTest(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })))
        }

        getTest();
    }, [])


    return (
        <DragDropContext className='mx-5' onDragEnd={onDragEnd}>
        <div className="row  task-list">
            {/* start of task list */}
            <div className="col-12">

                {/* start of task container */}
                    <Droppable
                        droppableId="allTask"
                        type="allTask"
                    >
                    {(provided, snapshot) => (
                        <TasksContainer
                            className='mx-5 h-100'
                            {...provided.droppableProps}
                            ref={provided.innerRef}
                            // isDraggingOver={snapshot.isDraggingOver}    
                        >
                            
                            {!allTasks && !allTasksList 
                            ? allTasksList.map((taskId, index) => 
                                <Task 
                                    key={allTasks[taskId].id}
                                    index={index}
                                    task={allTasks[taskId]}
                                    handleEditTask={handleEditTask}
                                    handleToggleDone={handleToggleDone}
                                    handleRemoveTask={handleRemoveTask}
                                />)
                            : console.log('h1')
                            }

                            {provided.placeholder}
                        </TasksContainer>  

                    )}

                    </Droppable>
        
                {/* end of task container */}

            </div>
            {/* end of task list */}
        </div>
        </DragDropContext>

    );
}

export default AllTaskList;
