import React from 'react';

export class Menu extends React.Component {
  constructor(props){
    super(props);
    this.handleClick = this.handleClick.bind(this);
  }

//Give Menu a new property, before the render function, named 
//handleClick. Set handleClick equal to a function with one 
//parameter named e.
//Inside of the body of handleClick, declare a new variable named text. 
//Set text equal to e.target.value. 
//This will equal the text of a clicked radio button.
  handleClick(e){
    const text = e.target.value;
    this.props.chooseVideo(text);
  }
//In the constructor of App, bind .chooseVideo() to the current value of this 
//and store it in this.chooseVideo.
  render() {
    return (
      <form onClick={this.handleClick}>
        <input type="radio" name="src" value="fast" /> fast
        <input type="radio" name="src" value="slow" /> slow
        <input type="radio" name="src" value="cute" /> cute
        <input type="radio" name="src" value="eek" /> eek
      </form>
    );
  }
}
