import React from 'react';
import ReactDOM from 'react-dom';
import {Testing} from './testing';

//In the constructor of App, bind .chooseVideo() to the current 
//value of this and store it in this.chooseVideo.
export class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      color: "",
    };
    this.boxClick = this.boxClick.bind(this);
  }

  boxClick(e){
    this.setState({
      color: "red"
    })
  }

  render() {
    return (
      <div className="App">


        <article className='experimentsHolder'>
          <h2>Test 3</h2>
          <p>This is an example of an onClick event 'renderd' by react.</p>
          <div className="boxClickCss"
            style={{ color: this.state.color }}
            onClick={this.boxClick}>Click Me!</div>
        </article>
        
        <Testing style={{ color: this.state.color }} onClick={this.boxClick}/>
      </div>)
  }
}


ReactDOM.render(
  <App />,
  document.getElementById('app')
);
