import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';

export class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      display: "block",
    };
    this.addList = this.addList.bind(this);
  }

  addList(){
    this.setState({
      display:"none"
    })
  }

  printInput(e){
    document.getElementById("result").innerHTML = document.getElementById('notesinput').value
  }

  render() {
    return (
      <div>
      <div>
        <button onClick={this.addList}>+</button>
      </div>
      <div className="col-6 d-none" style={this.state}>
        {/*<!-- Start of upper part: Title -->*/}
        <div className="row title">
          <p>New Task</p>
          <div className="col pb-2 border-bottom">
            <input className="mt-2 border-0 new-task-input" type="text" placeholder="New Task Title"></input>
          </div>
        </div>
        {/*<!-- End of upper part: Title -->*/}

        {/*<!-- Start of lower part: List -->*/}
        <div className="row">
          <div className="col mt-4 note-col">
            Notes
            <input className="w-100 add-task mt-5 subtitle" type="text" placeholder="Notes" id="notesinput"></input>
            <select className="w-100 add-task mt-5 notes-list" name="cars" id="list">
              <option value="" disabled selected>Assign to</option>
              <option value="Daily">Daily</option>
              <option value="grocesory">Grocesory</option>
              <option value="final-project">Final Project</option>
              <option value="work">Work</option>
            </select>
            <p className="font-weight-bold add-task border-0 mt-5 w-100 border-bottom pb-3 subtitle">
              Details</p>
            <div className="mt-5">
              Date
              <input className="w-100 add-task" type="text" placeholder="Date" onfocus="(this.type = 'date')"
                onblur="(this.type='text')"></input>
            </div>

            <div className="mt-5">
              Time
              <i className="fa-thin fa-alarm-clock small-icon"></i> <input className="add-task input-box" type="text"
                placeholder="Time" onfocus="(this.type = 'time')" onblur="(this.type='text')"
                ></input>

            </div>
            <div className="mt-5">
              Location
              <i className="fas fa-map-marker-alt small-icon" ></i>
              <input className="add-task ms-2 input-boxs" type="text" placeholder="Location"></input>

              <div className="col"></div>
            </div>
          </div>
        </div>
        {/*<!-- End of lower part: Task List -->*/}
        <button onClick={this.printInput}>++++</button>
        Name: <span id="result"></span>
      </div>
      </div>
    )
  }
}



ReactDOM.render(
  <App />,
  document.getElementById('app')
);
